//verifys if username exists and will return true/false 
function verifyUser($email)
{
    global $conn;
    $stmt = $conn->prepare('SELECT * FROM user WHERE email = ?');
    $stmt->bind_param('s', $email);
    $stmt->execute();
    return $stmt->fetch();
}
function getUserWithId($id)
{
    $result = query('SELECT * FROM user WHERE ID = ?', $id);
    $row = $result->fetch_assoc();
    return $row;
}
function getUserWithEmail($email)
{
    $result = query('SELECT * FROM user WHERE email = ? AND role != "passive"', $email);
    $row = $result->fetch_assoc();
    return $row;
}
function getName($id)
{
    global $conn;
    $stmt = $conn->prepare('SELECT firstName, lastName FROM user WHERE ID = ?');
    $stmt->bind_param('s', $id);
    $stmt->execute();
    $result = $stmt->get_result();
    $row = $result->fetch_assoc();
    return strtoupper($row["firstName"] . " " . $row["lastName"]);
}
//returns the role that a user have with help of a specifed username.
function getRole($id)
{
    $result =  query('SELECT role FROM user WHERE ID = ?', $id);
    $row = $result->fetch_assoc();
    return $row["role"];
}
