//Saves path from file that we later delete and will return the path that it saved to remove it from our folder.
function deleteFile($fileID)
{
    $result = query('SELECT path FROM file WHERE id = ?', $fileID);
    $row = $result->fetch_assoc();
    query('DELETE FROM file WHERE id = ?');
    return $row["path"];
}
//deletes user with specefied username
function deleteUser($username)
{
    query('DELETE FROM user WHERE username = ?', $username);
}
function makeId()
{
    $id = "";
    $alf = "abcdefghijklmnopqrstvwxyzABCDEFGHIJKLMNOPQRSTVWXYZ123467890";
    for ($i = 0; $i < 16; $i++) {
        $id .= $alf[rand(0, strlen($alf) - 1)];
    }
    return $id;
}
