    return $result;
}
function query($query)
{
    $s = "";
    global $conn;
    $list = array();
    $params = array();
    for ($i = 1; $i < func_num_args(); $i++) {
        if (gettype(func_get_arg($i)) == "integer") {
            $s .= "i";
        } else {
            $s .= "s";
        }

        array_push($params, mysqli_real_escape_string($conn, func_get_arg($i)));
    }
    $list[] = &$s;
    for ($i = 0; $i < count($params); $i++) {
        $list[] = &$params[$i];
    }
    $stmt = $conn->prepare($query);
    echo $conn->error;
    call_user_func_array(array($stmt, 'bind_param'), $list);
    $stmt->execute();
    $result = $stmt->get_result();
    echo $conn->error;
    return $result;
}


function addRequest($state, $post)
{
    global $conn;
    $dueDate = $post["dueDate"] != "" ? $post["dueDate"] : NULL;
    $dueTime = $post["dueTime"] != "" ? $post["dueTime"] : NULL;
    $source = $post["source"] == 0 ? "Ingen" : $post["source"];
    $mark = $post["mark"];
    $reference = $post["reference"];
    if (isset($post["eBilling"])) $eBilling = 1;
    else $eBilling = 0;
    $billingStreetname = $post["badress"];
    $billingStreetNumber = $post["badressNr"];
    $billingZipcode = (int) $post["zipcode"];
    $billingCity = $post["city"];
    $billingEmail = $post["fepost"];
    $billingReference = $post["reference"] == 0 ? "Ingen" : $post["reference"];
    $freeText = $post["freeText"];
    $notes = $post["notes"] == 0 ? "Ingen" : $post["notes"];
    $internalNotes = $post["internalNotes"];

    query('INSERT INTO Request (dueDate, dueTime, source, marking, reference, eInvoice, billingStreetname, billingStreetNumber, billingZipcode, billingCity, billingEmail, billingReference, notes, freeText, internalNotes, state) VALUES (?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?)', $dueDate, $dueTime, $source, $mark, $reference, $eBilling, $billingStreetname, $billingStreetNumber, $billingZipcode, $billingCity, $billingEmail, $billingReference, $freeText, $internalNotes, $notes, $state);
    $last_id = mysqli_insert_id($conn);
    $result = query('SELECT * FROM Request WHERE id = ?', $last_id)->fetch_assoc();
    // ChromePhp::log($result, $dueDate, $dueTime, $source, $mark, $reference, $eBilling, $billingStreetname, $billingStreetNumber, $billingZipcode, $billingCity, $billingEmail, $billingReference, $freeText, $internalNotes, $notes, $state);
    $i = 0;
    ChromePhp::log(isset($post["adressmove-from" . $i]), "adressmove-from" . $i);
    while (isset($post["adressmove-from" . $i])) {
        if (isset($post["bigElevatormove-from" . $i])) $post["bigElevatormove-from" . $i] = 1;
        else $post["bigElevatormove-from" . $i] = 0;
        if (isset($post["smallElevatormove-from" . $i])) $post["smallElevatormove-from" . $i] = 1;
        else $post["smallElevatormove-from" . $i] = 0;
        addAdress($post["adressmove-from" . $i], $post["adressNrmove-from" . $i], $post["zipcodemove-from" . $i], $post["citymove-from" . $i], $post["livingmove-from" . $i], $post["contactmove-from" . $i], $post["phonemove-from" . $i], $i, $post["bigElevatormove-from" . $i], $post["smallElevatormove-from" . $i], "move-from", $last_id);
        $i++;
    }
    while (isset($post["adressmove-to" . $i])) {
        if (isset($post["bigElevatormove-to" . $i])) $post["bigElevatormove-to" . $i] = 1;
        else $post["bigElevatormove-to" . $i] = 0;
        if (isset($post["smallElevatormove-to" . $i])) $post["smallElevatormove-to" . $i] = 1;
        else $post["smallElevatormove-to" . $i] = 0;
        addAdress($post["adressmove-to" . $i], $post["adressNrmove-to" . $i], $post["zipcodemove-to" . $i], $post["citymove-to" . $i], $post["livingmove-to" . $i], $post["contactmove-to" . $i], $post["phonemove-to" . $i], $i, $post["bigElevatormove-to" . $i], $post["smallElevatormove-to" . $i], "move-to", $last_id);
        $i++;
    }
}
function addAdress($streetName, $streetNumber, $zipcode, $city, $living, $contactPerson, $contactPhone, $position, $bigElevator, $smallElevator, $state, $id)
{
    global $conn;
    query('INSERT INTO adress (streetName, streetNumber, zipcode, city, living, contactPerson, contactPhone, position, bigElevator, smallElevator, state) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)', $streetName, $streetNumber, (int) $zipcode, $city, $living, $contactPerson, $contactPhone, $position, $bigElevator, $smallElevator, $state);
    $last_id = mysqli_insert_id($conn);
    query('INSERT INTO adressConn (adid, rid) VALUES (?, ?)', $last_id, $id);
}
